angular.module('powwow-ui-common').service('rpcService',
    ["app", "jsonrpc", "$interval", "$q", "queue", "requestResponseHandler", function (app, jsonrpc, $interval, $q, queue, requestResponseHandler) {
        var nameSpaces = {};

        this.initializeApp = function (initData) {
            app.call('.initialize', initData);
        };

        this.initializeScreen = function ($scope) {
        };

        this.request = function (method, params) {
            stopHeartbeat(method);
            callMethod(method, params).then(function (response) {
                if(response.nextScreen) {
                    var nextScreen = response.nextScreen;
                    delete response.nextScreen;
                    requestResponseHandler.processResponse(nextScreen, response, app);
                    // Removed this - logout shouldn't be hardcoded like this for Windows apps - Please check with Samir...
                    // if(nextScreen == "app.logout") {
                    //     app.openStartScreeen().then(app.hideLoading);
                    //     return;
                    // }

                    // At this point overrides have happened and we can start the heartbeat
                    startHeartbeat(method);
                }
            });
        };

        function callMethod(methodName, params) {
            var arr = methodName.split('.');
            var namespace = arr[0];
            var name = arr[1];

            var service = nameSpaces[namespace];
            if (!service) {
                service = jsonrpc.newService(namespace);
                nameSpaces[namespace] = service;
            }

            var method = service.createMethod(name);

            return $q(function (resolve, reject) {
                var item = queue.createItem(method, params, resolve, reject);
                queue.defer(item);
            });
        }

        function startHeartbeat(method) {
            if (method !== 'app.keepalive' && app.keepAliveTimeout > 0) {
                app.keepAliveInterval = $interval(function() {
                    app.call('app.keepalive', null, true);
                }, app.keepAliveInterval);
            }
        }

        function stopHeartbeat(method) {
            if (method !== 'app.keepalive' && app.keepAliveTimeout > 0) {
                $interval.cancel(app.keepAliveInterval);
            }
        }
    }]);

