angular.module('powwow-ui-common').provider('url',
    ["config", function (config) {
        var websocketConnection = true;

        var search = new URI(window.location.href).search(true);

        var env = search.env || config.defaultEnv;

        var rpcUrl, hasExplicitRpcUrl = false;

        // In order of precendence, the URL parameter can contain one of:

        // 1) "rpcUrl" parameter with value "mock" or "mock=<...>"
        if (search.hasOwnProperty('mock')) {
            rpcUrl = 'mock';
            websocketConnection = false;
            // 2) "rpcUrl" parameter with value "powwowLogin" or "powwowLogin=<...>"
        } else if (search.hasOwnProperty('powwowLogin')) {
            rpcUrl = 'powwowLogin';
            websocketConnection = true;
            // 3) "rpcUrl" parameter
        } else if (search.rpcUrl) {
            rpcUrl = search.rpcUrl;
            hasExplicitRpcUrl = true;
            //if rpcUrl is not like "http(s)://", it is webSocket connection
            websocketConnection = rpcUrl.indexOf("http") != 0;
            // 5) Otherwise default to ws://localhost:3000
        } else if (search.sessionURL) {
            // proceed without setting rpcUrl, it will be set by connectUsingSessionUrl
        } else {
            rpcUrl = 'ws://' + config.rpc.defaultHost + ':' + config.rpc.defaultPort;
        }

        // The URL will either contain "rpcUrl=<...>" with an optional "connectUrl=<...>" OR it will contain a "sessionURL=<...>".
        // The connectUrl/sessionURL are the same thing and can be queried to determine if the session is alive and to get the
        // rpcUrl.

        var connectUrl = search.connectUrl || search.sessionURL;

        if (!connectUrl && websocketConnection) {
            // If making a secure websocket connection, use https for connect url,
            // otherwise use http for connect url.
            var secureProtocol = rpcUrl && (rpcUrl.indexOf("wss:") == 0);
            // Match either "ws://host:port/sessions" or "wss://host:port/sessions"
            var connectHostMatch = rpcUrl.match(/wss?:\/\/(\S+)\/sessions/);
            var connectHost;
            if (connectHostMatch && connectHostMatch.length > 1) {
                connectHost = connectHostMatch[1];
            } else {
                connectHost = config.rpc.defaultHost + ':' + config.rpc.defaultPort;
            }
            var sessionIdMatch = null;
            if (rpcUrl.match(/\/sessions\/(\S[^\/]+)/)) {
                sessionIdMatch = rpcUrl.match(/\/sessions\/(\S[^\/]+)/);
            }
            var sessionId = '';
            if (sessionIdMatch && sessionIdMatch.length > 1) {
                sessionId = sessionIdMatch[1];
            }
            if(sessionId) {
                // Determine the connect URL from the websocket url.
                connectUrl = (secureProtocol ? 'https://' : 'http://') + connectHost + '/sessions/' + sessionId;
            }
        }

        var urlObj = {
            screen: search.screen ? search.screen : null,
            rpc: rpcUrl,
            hasExplicitRpcUrl: hasExplicitRpcUrl,
            websocketConnection: websocketConnection,
            connect: connectUrl,
            env: env
        };
        angular.extend(this, urlObj);
        this.$get = function () {
            return urlObj;
        }
    }]
);

angular.module('powwow-ui-common').service('viewport', ["config", "$window", function(config, $window) {
    'use strict';

    /**
     * Determine type of device
     */
    this.getType = function() {
        var search = new URI($window.location.href).search(true);
        if (search.hasOwnProperty('device')) {
            return search.device;
        } else {
            var isDesktop = $window.isPlatform.desktopBrowser();
            var isTablet = _getViewportDiagonal() > 1000;
            if (isDesktop) {
                return 'desktop';
            }
            return isTablet ? 'tablet' : 'phone';
        }
    };

    /**
     * Determine the form factor of the device
     */
    this.getFormFactor = function(screen) {
        if (!screen.extraViews || !screen.extraViews.length) {
            return 'default';
        }
        if (this.getType() === 'desktop' && screen.extraViews.indexOf('desktop') !== -1) {
            return 'desktop';
        }
        var orientation = window.innerHeight >= window.innerWidth ? 'portrait' : 'landscape';
        var formFactor = this.getType() + '-' + orientation;
        if (screen.extraViews.indexOf(formFactor) !== -1) {
            return formFactor;
        }
        formFactor = formFactor.replace('landscape', 'portrait');
        if (screen.extraViews.indexOf(formFactor) !== -1) {
            return formFactor;
        }
        return 'default';
    };

    /**
     * Private function for getting diagonal of screen viewport
     */
    function _getViewportDiagonal() {
        var viewPortWidth = window.innerWidth;
        var viewPortHeight = window.innerHeight;

        return Math.round(Math.sqrt(Math.pow(viewPortWidth, 2) + Math.pow(viewPortHeight, 2)));
    }

}]);

