angular.module('powwow-ui-common').service('ws', ["url", "screens", "$ionicLoading", "$http", "$state", "$ionicPopup", "$injector", "config", "$cookies", function (
    url, screens, $ionicLoading, $http, $state, $ionicPopup, $injector, config, $cookies
) {
    'use strict';

    // listeners for 'message' events registered outside:
    var messageListeners = [];
    var wsInstance = null;

    var isConnecting = false;
    var initParams = null;
    var numConnectionTries = 0;

    this.init = function (params) {
        if (!wsInstance) {
            $ionicLoading.show({ template: config.websocket.initializingLoadingTemplate });
            initParams = params;
            if (url.rpc) {
                wsInstance = new WebSocket(url.rpc);
                addListeners(wsInstance);
            } else if (url.connect) {
                connectUsingSessionUrl();
            }
        }
    };

    this.getInstance = function () {
        return wsInstance;
    };

    this.onMessage = function (listener) {
        messageListeners.push(listener);
    };

    this.reconnect = function () {
        doReconnect(true);
    };

    function unableToAccess() {
        var app = $injector.get('app');
        if ((typeof cordova === "object") && cordova.PowwowPlugin && !app.newPowwowLogin) {
            cordova.PowwowPlugin.doRecreateSession();
        } else {
            $ionicLoading.hide();
            $ionicPopup.confirm({
                title: 'Connection Error',
                template: "Unable to access " + url.connect,
                okText: "Retry",
                cancelText: "Cancel"
            }).then(function (retry) {
                if (retry) {
                    returnToLaunch();
                } else if ($injector.has('app')) {
                    var app = $injector.get('app');
                    app.screen = 'app.error';
                    app.data = {
                        message: "Unable to access " + url.connect
                    };
                    app.go('app.error');
                }
            });
        }
    }

    function connectUsingSessionUrl() {
        $http.get(url.connect).then(function (response) {
            if (response.data.status === 'connecting' && numConnectionTries < config.websocket.maxConnectionRetries) {
                numConnectionTries++;
                console.debug("Still connecting... Trying to connect again, retry #", numConnectionTries, "url:", url.connect);
                setTimeout(connectUsingSessionUrl, config.websocket.timeBetweenRetries);
            } else if (response.data.status === 'connected') {
                if (!url.hasExplicitRpcUrl) { url.rpc = response.data.url; }
                // If we're still connected, there must have been some hiccup in the connected,
                // try reconnecting.
                console.debug('Session is connected, connect websocket');
                doReconnect();
            } else {
                // Otherwise, the session isn't valid, logout!
                console.debug('Session is not valid, logging out');
                unableToAccess();
            }
        }, function () {
            console.debug('Unable to connect to server, trying again.');
            unableToAccess();
        });
    }


    // add listeners to WS events:
    function addListeners(ws, reloadOnClose) {
        ws.addEventListener('open', function () {
            console.debug('Connected to ' + url.rpc);

            ws.send(angular.toJson({
                jsonrpc: '2.0',
                method: ".initialize",
                params: initParams
            }));

            $ionicLoading.show({
                template: "<ion-spinner></ion-spinner><div>Initializing...</div>"
            });
            messageListeners.forEach(function (listener) {
                ws.addEventListener('message', listener);
            });
        });
        var restartOnClose = reloadOnClose;
        ws.addEventListener('close', function (closeEvent) {
            if (url.rpc === 'powwowLogin') {
                return;
            }
            var app = $injector.get('app');
            if (restartOnClose && !app.newPowwowLogin) {
                //If rpc url is localhost(as in proxy)
                // and host can not be resolved (code 1006)
                // and cordova is defined
                if ((url.rpc.indexOf('localhost') !== -1 || url.rpc.indexOf('127.0.0.1') !== -1) &&
                    closeEvent.code == 1006 &&
                    typeof cordova === "object" &&
                    cordova.PowwowPlugin
                ) {
                    cordova.PowwowPlugin.doRecreateSession();
                    return;
                }
            }
            var code = closeEvent.code;
            var reason = closeEvent.reason;

            // Workaround for bug where we may not properly go to a state when we lose a connection because we can't retrieve the template.
            if ($injector.has('app')) {
                var app = $injector.get('app');
                app.screen = null; // We are disconnected, set client state to null.
            }

            console.debug('Websocket connection closed with reason ' + reason + ' and code ' + code);
            if (code === 1000 && window.location.hash == 'updateSources') {
                window.location.hash = '';
                return;
            }
            // check session status with connect Url.
			// added check on reason because UWP app doesn't support closeEvent.code correctly.
            if (code === 4005 || reason === "Already connected") {
                alreadyConnected();
            } else if(url.connect) {
                updateSessionStatus();
            } else if(url.rpc) {
                doReconnect();
            }
        });
    };

    /**
     * Show confirmation popup if user has been connected from another device
     * If confirmed, user is reconnected
     * if not, user is logged out
     */
    function alreadyConnected() {
        var confirmPopup = $ionicPopup.confirm({
            title: config.websocket.alreadyConnectedPopupTitle,
            template: config.websocket.alreadyConnectedPopupTemplate
        });

        confirmPopup.then(function (res) {
            if (res) {
                updateSessionStatus();
            } else {
                var app = $injector.get('app');
                if ((typeof cordova === "object") && cordova.PowwowPlugin && !app.newPowwowLogin) {
                    cordova.PowwowPlugin.doUserLogout();
                } else {
                    app.logout(true);
                }
            }
        });
    }

    /**
     * Return to the login screen
     */
    function returnToLaunch() {
        var search = new URI(window.location.href).search(true);
        if (search.sessionURL) {
            delete search.sessionURL;
            delete search.endSessionURL;
            window.location.href = new URI("/launch").search(search).toString();
        } else {
            window.location.reload();
        }
    }

    /**
     * Update session status using connectURL
     * if session is connected, websocket is automatically reconnected to it
     * if not, it sends message to the native app if application is running wrapped in native application
     * if connectURL is not reachable, application tries to reconnect to the last websocket url
     */
    function updateSessionStatus() {
        if (!url.connect) {
            doReconnect();
            return;
        }
        $http({
            url: url.connect,
            headers: {
                "Authorization": 'bearer ' + $cookies.get('token')
            },
            withCredentials: true,
            method: 'GET'
        }).then(function (response) {
            var sessionExists = response.data.status === 'connected';
            if (sessionExists) {
                // If we're still connected, there must have been some hiccup in the connected,
                // try reconnecting.
                console.debug('Session is still connected, trying to reconnect websocket');
                doReconnect();
            } else {
                // Otherwise, the session isn't valid, logout!
                console.debug('Session is invalid, logging out');
                var app = $injector.get('app');
                if ((typeof cordova === "object") && cordova.PowwowPlugin && location.protocol === 'file:' && !app.newPowwowLogin) {
                    cordova.PowwowPlugin.doRecreateSession();
                } else {
                    console.log("Unable to contact server.");
                    returnToLaunch();
                }
            }
        }, function (error) {
            if (error.status == 404 || error.status == 401) {
                var powwowLoginNew = $injector.get('powwowLoginNew');
                powwowLoginNew.clearCachedCredentials();
                window.location.reload();
            } else {
                // If the connection failed, try reconnecting again.
                console.debug('Unable to reach connect Url, trying to reconnect websocket');
                doReconnect();
            }
        });
    }

    function doReconnect(isReconnecting) {
        if (!isConnecting && !isReconnecting) {
            $ionicLoading.show({ template: config.websocket.reconnectingLoadingTemplate });
            isConnecting = true;
        }
        wsInstance = new WebSocket(url.rpc);
        wsInstance.addEventListener('open', function () {
            $ionicLoading.hide();
            isConnecting = false;
        });
        addListeners(wsInstance, true);
    }

}]);

angular.module('powwow-ui-common').directive('powwowChange', ["app", function (app) {
    'use strict';

    /**
     * Usage example:
     * 
     * <input  type="text" ng-model="data.username" powwow-change="{dynamic: true, timeout: 2000}">
     *   or:
     * <select-list ng-model="data.state" powwow-change="{dynamic: false}">State</select-list>
     *   or:
     * <ion-checkbox ng-model="data.checked" powwow-change="{dynamic: false}">State</ion-checkbox>
     *
     * "field" - String representing the data to be updated.  If this is not specified, ng-model is used.
     *           If ng-model doesn't start with "data.", this is required.  This has the same format as
     *           the action string in app.action, e.g. "details[item].checked".
     * 
     * "dynamic" - indicates to the server if we are waiting for a DOM mutation or not.  Dynamic means
     *             this field is part of a dynamic form.
     * 
     * "timeout" - For dynamic fields, while we are expecting the form to change based on the selection,
     *             it is possible that nothing changes, and instead of getting "stuck" waiting for a change
     *             that will never come, we will timeout after the specified time.
     * 
     * This directive will do the following:
     * 
     * - Track when an onchange event happens.
     * 
     * - For text fields, the change will correspond to a blur event.
     * 
     * - When the change happens, it will call the field update call.
     *  app.call("app.update", {'model': '<modelName>', dynamic: true, timeout: <nnn>, data: {'field', value } })
     * 
     * - It will handle making sure the field is updated before any subsequent actions get called in
     *   situations where the user clicks the action button while the field has focus.
     */

    function parseModelString(str) {
        var end, arrParsed = [];
        while ((end = str.lastIndexOf(']')) != -1) {
            var woEnd = str.substring(0, end);
            var start = woEnd.lastIndexOf('[');
            if (start != -1) {
                var objName = str.substring(start + 1, end);
                var remaining = str.substring(end + 1);
                str = str.substring(0, start);
                arrParsed.push({ indexObject: objName, right: remaining })
            } else {
                throw ("Syntax error in model string.")
            }
        }
        arrParsed.push({ left: str });
        return arrParsed;
    }

    function replaceObjectsWithIndices(fieldModel, scope) {
        fieldModel = fieldModel.match(/\.(.*)/)[1];
        var arrParsed = parseModelString(fieldModel);
        var updatedFieldModel = "";
        while (scope && scope.hasOwnProperty('$parent') && arrParsed.length > 0) {
            var entry = arrParsed[0];
            if (entry.hasOwnProperty('indexObject')) {
                if (scope.hasOwnProperty(entry.indexObject) && scope[entry.indexObject].hasOwnProperty('$i')) {
                    // We found the index!
                    updatedFieldModel = "[" + scope[entry.indexObject]['$i'] + "]" + entry.right + updatedFieldModel;
                    arrParsed.shift();
                }
            } else if (entry.hasOwnProperty('left')) {
                //if (scope.hasOwnProperty('data')) {
                    updatedFieldModel = "data." + entry.left + updatedFieldModel;
                    arrParsed.shift();
                //}
            }
            scope = scope.$parent;
        }
        return updatedFieldModel;
    }

    function isTextField(el) {
        if (el[0].nodeName == "INPUT" && (el[0].type == "text" || el[0].type == "password")) {
            return true;
        }
        if (el[0].nodeName == "TEXTAREA") {
            return true;
        }
        return false;
    }

    return {
        restrict: 'A',
        require: '?ngModel',
        link: function (scope, el, attrs, ngModelController) {
            var fieldModel = attrs.ngModel;
            var params;
            try {
                eval("params = " + attrs.powwowChange)
            } catch (e) {
                params = {};
            }
            if (params.field) {
                fieldModel = params.field;
            }
            // Replace any index object in the field model with real values.
            fieldModel = replaceObjectsWithIndices(fieldModel, scope.$parent);
            if (isTextField(el)) {
                ngModelController.$options = { updateOn: 'blur' };
                el.on('focus', function () {
                    app.setFieldChangePending(true);
                });
                el.on('blur', function () {
                    app.setFieldChangePending(false);
                });
            }
            ngModelController.$viewChangeListeners.push(function () {
                app.updateField(fieldModel, ngModelController.$viewValue, params);
            });
            scope.$on('$destroy', function () {
                // Remove any event handlers.
                el.off();
            });
        }
    };
}]);

angular.module('app').filter('highlight', function() {
    'use strict';

    return function(text, search, caseSensitive) {
        if (text && (search || angular.isNumber(search))) {
            text = text.toString();
            search = search.toString();
            if (caseSensitive) {
                return text.split(search).join('<span class="ui-match">' + search + '</span>');
            } else {
                return text.replace(new RegExp(search, 'gi'), '<span class="ui-match">$&</span>');
            }
        } else {
            return text;
        }
    };
});

